import React from 'react';

class TempUnitButton extends React.Component {
  handleClick = (e) => {
    this.props.setUnit(e.target.getAttribute("name")[0]);
  }
  render() {
    return (
      <div className="tempButtonContainer">
        {this.props.tempUnit==='c' &&
        <div className="tempButton">
          <button type="button" name="celcius" onClick={this.handleClick} className="buttonFocus">&#8451;</button>
          <button type="button" name="farenheit" onClick={this.handleClick}>&#8457;</button>
        </div>
        }
        {this.props.tempUnit==='f' &&
        <div className="tempButton">
          <button type="button" name="celcius" onClick={this.handleClick}>&#8451;</button>
          <button type="button" name="farenheit" onClick={this.handleClick} className="buttonFocus">&#8457;</button>
        </div>
        }
      </div>
    );
  }
}

export default TempUnitButton;
