import React from 'react';

class TempUnitButton extends React.Component {
  render() {
    return (
      <div className="tempButtonContainer">
        {this.props.tempUnit==='c' &&
        <div className="tempButton">
          <button type="button" name="celcius" onClick={this.props.setUnit('c')}>&#8451;</button>
          <button type="button" name="farenheit" onClick={this.props.setUnit('f')}>&#8457;</button>
        </div>
        }
        {this.props.tempUnit==='f' &&
        <div className="tempButton">
          <button type="button" name="celcius" onClick={this.props.setUnit('c')}>&#8451;</button>
          <button type="button" name="farenheit" onClick={this.props.setUnit('f')}>&#8457;</button>
        </div>
        }
      </div>
    );
  }
}

export default TempUnitButton;
