        // this.handleCountryChange = this.handleCountryChange.bind(this);
        this.newData = this.newData.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(zipCode,countryCode) {
        this.setState({
            zipCode: zipCode,
            countryCode: countryCode
        })
    }
