import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import city from './city.js';

class Weather extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name : "",
            weather : {},
        };
    }
    componentDidMount() {
        const city = this.props.city;
        const url = 'https://api.openweathermap.org/data/2.5/weather?q='+city+'&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((data) => {this.setState({
            name: data.weather[0].id,
            weather: data.weather[0],
            })
        });
    }
    render() {
        const { name, weather} = this.state;
        //console.log(weather.timezone);
        return(
            <ul>
                <li key={name}>
                    {weather.description}

                </li>
            </ul>
        );
    }
}

ReactDOM.render(
    <Weather city={city[2].name}/>,
    document.getElementById("root")
);
