import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import city from './city.js';

class Weather extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: {}
        };
    }
    componentDidMount() {
        const city = this.props.city;
        const url = 'https://api.openweathermap.org/data/2.5/weather?q='+city+'&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((data) => {this.setState({
            data : data,
            })
        });
    }
    render() {
        const data = this.state.data;
        console.log(data);
        return(
            <h1>{data.id}</h1>
        );
    }
}

ReactDOM.render(
    <Weather city="London"/>,
    document.getElementById("root")
);
