import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e) {
        this.props.onSubmit(e.target[0].value,e.target[1].value);
        e.preventDefault();
    }

    render() {
        return(
            <div className="search">
                <form  onSubmit={this.handleSubmit}>
                    <input type="text" placeholder="Zip Code" ref="zip"/>
                    <input type="text" placeholder="Country Code" ref="country" />
                    <input type="submit" value="Search" />
                </form>
            </div>
        );
    }
}

class  WeatherTitle extends React.Component {
    render() {
        if(this.props.data.main) {
            return (
                <h1>Weather in {this.props.data.name},{this.props.data.sys.country}</h1>
            );
        } else return null;
    }
}

class WeatherTable extends React.Component {
    render() {
        if(this.props.data.main) {
            return(
                <table className="tempPress">
                    <caption>Weather</caption>
                    <tr>
                        <th>Temperature</th>
                        <th>{(this.props.data.main.temp-273).toFixed(2)} C</th>
                    </tr>
                    <tr>
                        <th>Pressure</th>
                        <th>{(this.props.data.main.pressure/1000).toFixed(2)} bar</th>
                    </tr>
                    {/* <tr>
                        <th>Humidity</th>
                        <th>{this.props.data.main.humidity}%</th>
                    </tr> */}
                    <tr>
                        <th>Minimum Temp</th>
                        <th>{(this.props.data.main.temp_min-273).toFixed(2)} C</th>
                    </tr>
                    <tr>
                        <th>Maximum Temp</th>
                        <th>{(this.props.data.main.temp_max-273).toFixed(2)} C</th>
                    </tr>
                    <tr>
                        <th>Cloudiness</th>
                        <th>{this.props.data.weather[0].description}</th>
                    </tr>
                </table>
            );
        } else return null;
    }
}

class WeatherData extends React.Component {
    constructor(props) {
        super(props);
        this.newData = this.newData.bind(this);
        this.getMyLocation = this.getMyLocation.bind(this);
        this.getMyLocation();
    }

    getMyLocation() {
        this.props.getMyLocation();
    }

    fetchDataByZip(zipCode,countryCode) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?zip=' + zipCode + ',' + countryCode + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((wdata) => {
            this.newData(wdata);
        });
    }

    newData(data) {
        this.props.newData(data);
    }

    fetchDataByGeo(latitude,longitude) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?lat=' + latitude + '&lon=' + longitude + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((wdata) => {
            this.newData(wdata);
        });
    }

    componentDidMount() {
        if(this.props.zipCode && this.props.countryCode){
            this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        console.log(this.props.latitude);
        console.log(this.props.longitude);
        if(this.props.latitude!==null && this.props.longitude!==null){
            this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
    }


    componentDidUpdate(prevProps) {
        if (this.props.zipCode !== prevProps.zipCode || this.props.countryCode !== prevProps.countryCode) {
          this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        console.log(this.props.latitude);
        console.log(this.props.longitude);
        if (this.props.latitude !== prevProps.latitude || this.props.longitude !== prevProps.longitude) {
            this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
      }

    render() {
        const data = this.props.data;
        if(data) {
            // return(
            //     <h1>{data.id}</h1>
            // );
            return null;
        } else return null;
    }
}

let location,latitude,longitude;

class WeatherApp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            zipCode: "",
            countryCode: "",
            data: "",
        };
        this.newData = this.newData.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.getMyLocation = this.getMyLocation.bind(this);
    }
    getMyLocation() {
        if (window.navigator && window.navigator.geolocation) {
            location = window.navigator.geolocation
        }
        if (location){
            location.getCurrentPosition(function (position) {
                latitude = (position.coords.latitude);
                longitude = (position.coords.longitude);
            });
        }        
    }

    handleSubmit(zipCode,countryCode) {
        this.setState({
            zipCode: zipCode,
            countryCode: countryCode
        })
    }


    newData(data) {
        this.setState({
            data: data,
        })
    }

    render() {
        return(
            <div className="container">
                <SearchBar 
                    zipCode={this.state.zipCode}
                    countryCode={this.state.countryCode}
                    latitude={this.state.latitude}
                    longitude={this.state.longitude}
                    onSubmit={this.handleSubmit}
                />
                {/* <Clock /> */}
                <WeatherTitle zipCode={this.state.zipCode} countryCode={this.state.countryCode} data={this.state.data} />
                <WeatherTable data={this.state.data} />
                <WeatherData 
                    zipCode={this.state.zipCode} countryCode={this.state.countryCode} 
                    newData={this.newData}
                    data={this.state.data}
                    latitude={latitude}
                    longitude={longitude}
                    getMyLocation={this.getMyLocation}
                />
            </div>

        );
    }
}


ReactDOM.render(
    <WeatherApp />,
    document.getElementById("root")
);
