import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import city from './city.js';

class Weather extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            weather : [],
        };
    }

    componentDidlMount() {
        fetch('https://api.openweathermap.org/data/2.5/weather?q=London&appid=9b32cf97b04c03ad2e6f0960ea7bdce3')
        .then(results => {
            // results = JSON.parse(results);
            // let weather = results.weather;
            // this.setState({weather:weather});
            return results.json;
        }).then((data) => {
            let weather = data.weather;
            debugger;
            this.setState(
                {weather: weather}
            );
        });


    }
    render() {
        return(
            <h1>{this.state.weather}</h1>
        );
    }
}

ReactDOM.render(
    //<Weather city={city[1]} />,
    <h1>hello</h1>,
    document.getElementById("root")
);
