import React from 'react';


class LocationSearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data : null,
      queryText: "",
    };
  }

  handleChange = (e) => {
    if(e.target.value.length>0) {
      this.setState({
        queryText: e.target.value,
      });
      this.fetchDataByZip(e.target.value);
    } else {
      this.setState({
        data: null,
      });
    }
  };

  handleClick = (e) => {
    //console.log(e.target.key);
    //console.log(e.key);
  }

  fetchDataByZip = (queryText) => {
    const url = 'https://api.mapbox.com/geocoding/v5/mapbox.places/'+queryText+'.json?access_token=pk.eyJ1IjoidXNkYXJqaSIsImEiOiJjanhvbnNqY2QwODczM21ybnJybGUzM3l0In0.hUTTdbflff_BO_c7S3MldQ';
    fetch(url)
    .then(results => results.json())
    .then((sdata) => {
      this.setState({
        data : sdata,
      });
    });
  }

  render() {
    let options=[];
    const coordinates = {
      latitude: "",
      longitude: ""
    };
    if(this.state.data){

    for(let i=0; i<this.state.data.features.length; i++) {
      coordinates.latitude=this.state.data.features[i].geometry.coordinates[0];
      coordinates.longitude=this.state.data.features[i].geometry.coordinates[1];
      let indexKey= (String(coordinates.latitude)+" "+String(coordinates.longitude));
      options[i]=<li indexkey={indexKey} key={this.state.data.features[i].id} onClick={this.handleClick}>{this.state.data.features[i]["place_name"]}</li>
    } 
  }

    return (
      <div className="locationSearchBar">
        <input type="text" placeholder="Location" onChange={this.handleChange} />
        <ul>
          {options}
        </ul>
      </div>
    );
  }
}

export default LocationSearchBar;
