import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import wallpaper from './wallpaper.js';

//Components
//import SearchBar from './components/SearchBar/SearchBar.js';
import WeatherTitle from './components/WeatherTitle/WeatherTitle.js';
import Clock from './components/Clock/Clock.js';
import Cloudiness from './components/Cloudiness/Cloudiness.js';
import WeatherIcon from './components/WeatherIcon/WeatherIcon.js';
import Temperature from './components/Temperature/Temperature.js';
import OtherWeatherData from './components/OtherWeatherData/OtherWeatherData.js';
import WeatherData from './components/WeatherData/WeatherData.js';
import LocationSearchBar from './components/LocationSearchBar/LocationSearchBar.js';
//import { threadId } from 'worker_threads';
//Components


//let location,latitude,longitude;
//let location,latitude,longitude;
class WeatherApp extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      latitude: this.getMyLocation().latitude,
      longitude: this.getMyLocation().longitude,
      data: "",
      dayNight: "",
    };
  }

  getMyLocation = () => {
    let location = null;
    let latitude = null;
    let longitude = null;
    if (window.navigator && window.navigator.geolocation) {
      location = window.navigator.geolocation
    }
    if (location){
      location.getCurrentPosition(function (position) {
        latitude = (position.coords.latitude);
        longitude = (position.coords.longitude);
        // console.log(latitude);
        // console.log(longitude);
      });
      return ({
        latitude: latitude,
        longitude: longitude
      });
    }
    // this.setLatLong(latitude,longitude);        
  }  

  setLatLong = (latitude,longitude) => {
    this.setState({
      latitude: latitude,
      longitude: longitude,
    });
  };

  newData = (data) => {
    this.setState({
      data: data,
    })
  }

  day_Night = (time) => {
    this.setState({
      dayNight: time,
    })
  }

  componentDidMount() {
    if(!this.state.latitude) {
      this.setLatLong(this.getMyLocation().latitude,this.getMyLocation().longitude);
      console.log(this.state.latitude);
        console.log(this.state.longitude);
    }
  }

  render() {
    if (this.state.data.main){
      let url = wallpaper(this.state.data.weather[0].id);
      document.getElementById("root").style.backgroundImage = "url("+url+")";
    }
    return(
      <div className="rootContainer">
        <WeatherData 
          zipCode={this.state.zipCode} countryCode={this.state.countryCode} 
          newData={this.newData}
          data={this.state.data}
          latitude={this.state.latitude}
          longitude={this.state.longitude}
        />
        {/* <SearchBar 
            zipCode={this.state.zipCode}
            countryCode={this.state.countryCode}
            latitude={this.state.latitude}
            longitude={this.state.longitude}
            onSubmit={this.handleSubmit}
          /> */}
          <LocationSearchBar setLatLong={this.setLatLong} />
        {(this.state.data.main)!==undefined || <div className="lds-hourglass"></div>}
        {this.state.data.main &&
        <div className='Container'>

          <WeatherTitle zipCode={this.state.zipCode} countryCode={this.state.countryCode} data={this.state.data} />

          <div className='leftBlock'>
            <Clock data={this.state.data} day_Night={this.day_Night}/>

            <Cloudiness 
              data={this.state.data}
            />

            <WeatherIcon 
              data={this.state.data}
              dayNight={this.state.dayNight} 
            />

            <Temperature data={this.state.data} />
          </div>

          <OtherWeatherData data={this.state.data} />



        </div>
        }
      </div>
    );
  }
}


ReactDOM.render(
    <WeatherApp />,
    document.getElementById("root")
);
