import React from 'react';


class LocationSearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data : null,
      queryText: "",
    };
  }

  handleChange = (e) => {
    if(e.target.value.length>=1) {
      this.setState({
        queryText: e.target.value,
      });
      this.fetchDataByZip(e.target.value);
    }
  };

  fetchDataByZip = (queryText) => {
    const url = 'https://api.mapbox.com/geocoding/v5/mapbox.places/'+queryText+'.json?access_token=pk.eyJ1IjoidXNkYXJqaSIsImEiOiJjanhvbnNqY2QwODczM21ybnJybGUzM3l0In0.hUTTdbflff_BO_c7S3MldQ';
    fetch(url)
    .then(results => results.json())
    .then((sdata) => {
      this.setState({
        data : sdata,
      });
    });
  }

  render() {
    let options=[];
    //console.log(Boolean(this.state.data));
    if(this.state.data){
      // console.log(1);
    for(let i=0; i<this.state.data.features.length; i++) {
      options[i]=<li key={this.state.data.features[i].id}>{this.state.data.features[i]["place_name"]}</li>
      // console.log(options[i]);

    } 
  }
  //console.log(Boolean(this.state.data));
    return (
      <div className="locationSearchBar">
        <input type="text" placeholder="Location" onChange={this.handleChange}/>
        <ul>

            {/* {this.state.data && this.state.data.features[0]["place_name"]} */}
            {options}

        </ul>
      </div>
    );
  }
}

export default LocationSearchBar;
