import React from 'react';


class LocationSearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data : null,
      queryText: "",
      options: [],
    };
  }

  handleChange = (e) => {
    if(e.target.value.length>0) {
      this.fetchDataByText(e.target.value);
      // let options = [];
      // for(let i=0; i<5; i++) {
      //   options[i].push(this.data.features[i]["place_name"]);
      // }
      this.setState({
        queryText: e.target.value,
        options: this.state.data.features,
      });
    } else {
      this.setState({
        data: null,
        queryText: "",
      });
    }
  };

  handleClick = (e) => {
    //let target= e.target.getAttribute('indexkey');
    // for(let key in target){
    // console.log(target[key]);}
    let latitude = e.target.getAttribute('latitude');
    let longitude = e.target.getAttribute('longitude');
    this.props.setLatLong(latitude,longitude);
    this.setState({
      queryText: e.target.getAttribute('text'),
      options: [],
    });
  }

  fetchDataByText = (queryText) => {
    const url = 'https://api.mapbox.com/geocoding/v5/mapbox.places/'+queryText+'.json?access_token=pk.eyJ1IjoidXNkYXJqaSIsImEiOiJjanhvbnNqY2QwODczM21ybnJybGUzM3l0In0.hUTTdbflff_BO_c7S3MldQ';
    fetch(url)
    .then(results => results.json())
    .then((sdata) => {
      this.setState({
        data : sdata,
      });
    });
  }

  render() {
    let options=[];
    const coordinates = {
      latitude: "",
      longitude: ""
    };
    if(this.state.data){

    for(let i=0; i<this.state.data.features.length; i++) {
      coordinates.latitude=this.state.data.features[i].geometry.coordinates[0];
      coordinates.longitude=this.state.data.features[i].geometry.coordinates[1];
      let data= this.state.data.features[i];
      //console.log(indexKey);
      options[i]=<li 
                  latitude={data.geometry.coordinates[1]}
                  longitude={data.geometry.coordinates[0]}
                  text={data.text}
                  key={this.state.data.features[i].id}
                  onClick={this.handleClick}
                  >
                  {this.state.data.features[i]["place_name"]}
                </li>
    } 
  }

    return (
      <div className="locationSearchBar">
        <input type="text" onChange={this.handleChange} value={this.state.queryText} />
        <ul className="searchList">
          <li>
            {this.state.options}
          </li>
        </ul>
      </div>
    );
  }
}

export default LocationSearchBar;
