import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.handleZipChange = this.handleZipChange.bind(this);
        this.handleCountryChange = this.handleCountryChange.bind(this);
    }

    handleZipChange(e) {
        this.props.onZipChange(e.target.value);
    }

    handleCountryChange(e) {
        this.props.onCountryChange(e.target.value);
    }

    render() {
        return(
            <div className="search">
                <input type="text" placeholder="Zip Code" value={this.props.zipCode} onChange={this.handleZipChange} />
                <input type="text" placeholder="Country Code" value={this.props.countryCode} onChange={this.handleCountryChange} />
            </div>
        );
    }
}

class  WeatherTitle extends React.Component {
    render() {
        return (
            <h1>Weather in {this.props.zipCode},{this.props.countryCode}</h1>
        );
    }
}

class WeatherData extends React.Component {
    constructor(props) {
        super(props);
        this.data = {};
    }
    fetchDataByZip(zipCode,countryCode) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?zip=' + zipCode + ',' + countryCode + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((data) => {return data});
    }

    fetchDataByGeo(latitude,longitude) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?lat=' + latitude + '&lon=' + longitude + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((data) => {return data});
    }

    // componentDidMount() {
    //     const latitude = geolocation.latitude;
    //     const longitude = geolocation.longitude;
    //     if (latitude!== null){
    //         this.data = this.fetchDataByGeo(latitude,longitude);
    //     }
    // }


    componentDidUpdate(prevProps) {
        if (this.props.zipCode !== prevProps.zipCode || this.props.countryCode !== prevProps.countryCode) {
          this.data = this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        if (this.props.latitude !== prevProps.latitude || this.props.longitude !== prevProps.longitude) {
            this.data = this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
      }

    render() {
        const data = this.props.data;
        if(this.data.weather) {
            return(
                <h1>{data.name}</h1>
            );
        } else return null;
        // return null;
    }
}

// let geolocation = {
//     location: null,
//     latitude: null,
//     longitude: null,
// }

class WeatherApp extends React.Component {
    constructor(props) {
        super(props);

        //this.getMyLocation();
        this.state = {
            zipCode: "",
            countryCode: "",
            latitude: this.getMyLocation.latitude,
            longitude: this.getMyLocation.longitude,
        };
        this.getMyLocation();
        console.log(this.state.latitude);
        console.log(this.state.longitude);
        this.handleZipChange = this.handleZipChange.bind(this);
        this.handleCountryChange = this.handleCountryChange.bind(this);
    }

    getMyLocation = (e) => {
        let location = null;
        let latitude = null;
        let longitude = null;
        // if (geolocation.location == null) {
            if (window.navigator && window.navigator.geolocation) {
                location = window.navigator.geolocation
            }
            if (location){
                location.getCurrentPosition(function (position) {
                    latitude = Math.round(position.coords.latitude);
                    longitude= Math.round(position.coords.longitude);
                    console.log(latitude);
                    console.log(longitude);
                    // this.setState({
                    //     latitude: Math.round(position.coords.latitude),
                    //     longitude: Math.round(position.coords.longitude),
                    // });
                })
            }
            return ({
                latitude: latitude,
                longitude: longitude,
            });
        // }

    }

    handleZipChange(zipCode) {
        this.setState({
          zipCode: zipCode,  
        })
    }

    handleCountryChange(countryCode) {
        this.setState({
          countryCode: countryCode,  
        })
    }

    render() {
        return(
            <div className="container">
                <SearchBar 
                    zipCode={this.state.zipCode}
                    countryCode={this.state.countryCode}
                    latitude={this.state.latitude}
                    longitude={this.state.longitude}
                    onZipChange={this.handleZipChange}
                    onCountryChange={this.handleCountryChange}
                />
                {/* <Clock /> */}
                <WeatherTitle zipCode={this.state.zipCode} countryCode={this.state.countryCode} />
                <WeatherData zipCode={this.state.zipCode} countryCode={this.state.countryCode} />
            </div>

        );
    }
}


ReactDOM.render(
    //<SearchBar />,
    <WeatherApp />,
    document.getElementById("root")
);
