import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import city from './city.js';

class WeatherData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: {}
        };
    }
    componentDidMount() {
        const zipCode = this.props.zipCode;
        const countryCode = this.props.countryCode;
        const url = 'https://api.openweathermap.org/data/2.5/weather?zip='+zipCode+','+countryCode+'&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((data) => {this.setState({
            data : data,
            })
        });
    }
    render() {
        // const data = this.state.data;
        // if(data.weather) {
        //     return(
        //         <h1>{data.name}</h1>
        //     );
        // } else return null;
        return null;
    }
}

class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.handleSearchTextChange = this.handleSearchTextChange.bind(this);
    }

    handleSearchTextChange(e) {
        this.props.onSearchTextChange(e.target.value);
    }

    render() {
        return(
            <div className="search">
                <input type="text" placeholder="Zip Code" onChange={this.handleSearchTextChange} />
            </div>
        );
    }
}

class  WeatherTitle extends React.Component {
    render() {
        return (
            <h1>Weather in {this.props.cityName}</h1>
        );
    }
}

class WeatherApp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            searchText: "",
            //wdata: ""
        }
        this.handleSearchTextChange = this.handleSearchTextChange.bind(this);
    }

    handleSearchTextChange(searchText) {
        this.setState({
          searchText: searchText,  
        })
    }

    render() {

        return(
            <div className="container">
                <SearchBar />
                {/* <Clock />
                <WeatherTitle /> */}
                <WeatherData zipCode="560102" countryCode="in" />
            </div>
        );
    }
}


ReactDOM.render(
    //<SearchBar />,
    <WeatherApp />,
    document.getElementById("root")
);
