import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import city from './city.js';

class Weather extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            //id = {}
            weather : {},
        };
    }
    componentDidMount() {
        fetch('https://api.openweathermap.org/data/2.5/weather?q=London&appid=9b32cf97b04c03ad2e6f0960ea7bdce3')
        .then(results => results.json())
        .then((data) => {this.setState({weather: data.weather})});
    }
    render() {
        return(
            <h1>{this.state.weather}</h1>
        );
    }
}

ReactDOM.render(
    <Weather />,
    document.getElementById("root")
);
