import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import IconId from './icon.js'


class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e) {
        this.props.onSubmit(e.target[0].value,e.target[1].value);
        e.preventDefault();
    }

    render() {
        return(
            <div className="search">
                <form  onSubmit={this.handleSubmit}>
                    <input type="text" placeholder="Zip Code" ref="zip"/>
                    <input type="text" placeholder="Country Code" ref="country" />
                    <input type="submit" value="Search" />
                </form>
            </div>
        );
    }
}

class  WeatherTitle extends React.Component {
    render() {
        if(this.props.data.main) {
            return (
                <div className="weatherTitle">
                    <h1>{this.props.data.name}, {this.props.data.sys.country}</h1>
                </div>
            );
        } else return null;
    }
}

class Clock extends React.Component {

    componentDidMount() {
        this.timerID = setInterval(
          () => this.props.tick(),
          1000
        );
      }

    render() {
        let hours = this.props.date.getHours();
        let ampm = 'am';
        if (hours > 12) {
            hours = hours - 12;
            ampm = 'pm';
        } else if (hours === 0) {
            hours = 12;
            ampm = 'am'
        }
        if(this.props.data.main) {
            return (
                <div className="clock">
                    <h2>{this.props.date.toLocaleTimeString()}</h2>
                </div>
            );
        } else return null;
    }
}

class Cloudiness extends React.Component {
    render() {
        if(this.props.data.main) {
            let data = "";
            let arr = this.props.data.weather[0].description.split(" ");
            for (let i = 0; i < arr.length; i++) {
                data+=arr[i][0].toUpperCase() + arr[i].slice(1) + " ";
            }
            return(
                <div className="cloudiness">
                    <p>{data}</p>
                </div>
            );
        } else return null;
    }
}

class Icon extends React.Component {
    render() {
        if(this.props.data.main) {
            const id = IconId[this.props.data.weather[0].id];
            const url = "http://openweathermap.org/img/wn/" + id + "d@2x.png";
            return(
                <img src={url} alt={this.props.data.weather[0].id}/>
            );
        } else return null;
    }
}

class Temperature extends React.Component {
    render() {
        if(this.props.data.main) {
            return(
                <h1>{(this.props.data.main.temp-273).toFixed(2)} C</h1>
            );
        } else return null;
    }
}

class OtherData extends React.Component {
    render() {
        if(this.props.data.main) {
            return(
                <div className="otherData">
                    <p>Temperature: {this.props.data.main.temp_min} C - {this.props.data.main.temp_max} C</p>
                    <p>Humidity: {this.props.data.main.humidity} %</p>
                    <p>Wind: {this.props.data.wind.speed} km/hr</p>
                </div>
            );
        } else return null;
    }
}

class WeatherData extends React.Component {
    constructor(props) {
        super(props);
        this.newData = this.newData.bind(this);
        this.getMyLocation = this.getMyLocation.bind(this);
        this.getMyLocation();
    }

    getMyLocation() {
        this.props.getMyLocation();
    }

    fetchDataByZip(zipCode,countryCode) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?zip=' + zipCode + ',' + countryCode + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((wdata) => {
            this.newData(wdata);
        });
    }

    newData(data) {
        this.props.newData(data);
    }

    fetchDataByGeo(latitude,longitude) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?lat=' + latitude + '&lon=' + longitude + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((wdata) => {
            this.newData(wdata);
        });
    }

    componentDidMount() {
        if(this.props.zipCode && this.props.countryCode){
            this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        if(this.props.latitude!==null && this.props.longitude!==null){
            this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
    }


    componentDidUpdate(prevProps) {
        if (this.props.zipCode !== prevProps.zipCode || this.props.countryCode !== prevProps.countryCode) {
          this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        if (this.props.latitude !== prevProps.latitude || this.props.longitude !== prevProps.longitude) {
            this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
      }

    render() {
        const data = this.props.data;
        if(data) {
            // return(
            //     <h1>{data.id}</h1>
            // );
            return null;
        } else return null;
    }
}

let location,latitude,longitude;

class WeatherApp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            zipCode: "",
            countryCode: "",
            data: "",
            date: new Date(),
        };
        this.newData = this.newData.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.getMyLocation = this.getMyLocation.bind(this);
        this.tick = this.tick.bind(this);
    }
    getMyLocation() {
        if (window.navigator && window.navigator.geolocation) {
            location = window.navigator.geolocation
        }
        if (location){
            location.getCurrentPosition(function (position) {
                latitude = (position.coords.latitude);
                longitude = (position.coords.longitude);
            });
        }        
    }

    handleSubmit(zipCode,countryCode) {
        this.setState({
            zipCode: zipCode,
            countryCode: countryCode
        })
    }

    tick() {
        this.setState({
          date: new Date(),
        });
      }

    newData(data) {
        this.setState({
            data: data,
        })
    }

    render() {
        return(
            <div className="container">
                <SearchBar 
                    zipCode={this.state.zipCode}
                    countryCode={this.state.countryCode}
                    latitude={this.state.latitude}
                    longitude={this.state.longitude}
                    onSubmit={this.handleSubmit}
                />
                {/* <Clock /> */}
                <WeatherTitle zipCode={this.state.zipCode} countryCode={this.state.countryCode} data={this.state.data} />
                <Clock data={this.state.data} tick={this.tick} date={this.state.date} />
                <Cloudiness data={this.state.data} />
                <div className="iconTemp">
                    <Icon data={this.state.data} />
                    <Temperature data={this.state.data} />
                </div>
                <OtherData data={this.state.data} />
                <WeatherData 
                    zipCode={this.state.zipCode} countryCode={this.state.countryCode} 
                    newData={this.newData}
                    data={this.state.data}
                    latitude={latitude}
                    longitude={longitude}
                    getMyLocation={this.getMyLocation}
                />
            </div>

        );
    }
}


ReactDOM.render(
    <WeatherApp />,
    document.getElementById("root")
);
