import React from 'react';

class Temperature extends React.Component {
  render() {
    let temp, temp_min, temp_max;
    if(this.props.data.main) {
      if(this.props.tempUnit==='c') {
        temp = String((this.props.data.main.temp-273.15).toFixed(0)) +' \xB0C';
        temp_min = String((this.props.data.main.temp_min-273.15).toFixed(0)) +' \xB0C';
        temp_max = String((this.props.data.main.temp_max-273.15).toFixed(0)) +' \xB0C';
      }
      if(this.props.tempUnit==='f') {
        temp = String(((this.props.data.main.temp*9/5)-459.67).toFixed(0)) +' \xB0F';
        temp_min = String(((this.props.data.main.temp_min*9/5)-459.67).toFixed(0)) +' \xB0F';
        temp_max = String(((this.props.data.main.temp_max*9/5)-459.67).toFixed(0)) +' \xB0F';
      }
      return(
        <div className='temp'>
          <p>{temp}</p>
          {this.props.data.main.temp_max-this.props.data.main.temp_min!==0 &&
          <p className="smaller">{temp_min} / {temp_max}</p>
          }
        </div>
      );
    } else return null;
  }
}

export default Temperature;
