class Cloudiness extends React.Component {
    render() {
        if(this.props.data.main) {
            let data = "";
            let arr = this.props.data.weather[0].description.split(" ");
            for (let i = 0; i < arr.length; i++) {
                data+=arr[i][0].toUpperCase() + arr[i].slice(1) + " ";
            }
            return(
                <div className="cloudiness">
                    <p>{data}</p>
                </div>
            );
        } else return null;
    }
}

class Icon extends React.Component {
    render() {
        if(this.props.data.main) {
            const id = IconId[this.props.data.weather[0].id];
            const url = "http://openweathermap.org/img/wn/" + id + "d@2x.png";
            return(
                <div className='icon'>
                    <img src={url} alt={this.props.data.weather[0].id}/>
                </div>
            );
        } else return null;
    }
}

class Temperature extends React.Component {
    render() {
        if(this.props.data.main) {
            return(
                <div className='temp'>
                    <p>{(this.props.data.main.temp-273).toFixed(2)} &#8451;</p>
                </div>
            );
        } else return null;
    }
}

class OtherData extends React.Component {
    render() {
        if(this.props.data.main) {
            return(
                <div className="otherData">
                    <p>Temperature: {(this.props.data.main.temp_min-273).toFixed(2)} &#8451; - {(this.props.data.main.temp_max-273).toFixed(2)} &#8451;</p>
                    <p>Humidity: {this.props.data.main.humidity} %</p>
                    <p>Wind: {this.props.data.wind.speed} km/hr</p>
                </div>
            );
        } else return null;
    }
}

class WeatherData extends React.Component {
    constructor(props) {
        super(props);
        this.newData = this.newData.bind(this);
        this.getMyLocation = this.getMyLocation.bind(this);
        this.getMyLocation();
    }

    getMyLocation() {
        this.props.getMyLocation();
    }

    fetchDataByZip(zipCode,countryCode) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?zip=' + zipCode + ',' + countryCode + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((wdata) => {
            this.newData(wdata);
        });
    }

    newData(data) {
        this.props.newData(data);
    }

    fetchDataByGeo(latitude,longitude) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?lat=' + latitude + '&lon=' + longitude + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((wdata) => {
            this.newData(wdata);
        });
    }

    componentDidMount() {
        if(this.props.zipCode && this.props.countryCode){
            this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        if(this.props.latitude!==null && this.props.longitude!==null){
            this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
    }


    componentDidUpdate(prevProps) {
        if (this.props.zipCode !== prevProps.zipCode || this.props.countryCode !== prevProps.countryCode) {
          this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        if (this.props.latitude !== prevProps.latitude || this.props.longitude !== prevProps.longitude) {
            this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
      }

    render() {
       //  const data = this.props.data;
        // while(!data) {
        //     return(
