import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import city from './city.js';

class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        // this.handleZipChange = this.handleZipChange.bind(this);
        // this.handleCountryChange = this.handleCountryChange.bind(this);
        this.handleLocationChange = this.handleLocationChange.bind(this);
    }

    // handleZipChange(e) {
    //     this.props.onZipChange(e.target.value);
    // }

    // handleCountryChange(e) {
    //     this.props.onCountryChange(e.target.value);
    // }

    handleLocationChange(e) {
        // this.props.onLocationChange(e.target.zipCode,e.target.countryCode);
        this.props.onLocationChange(this.zipCode.zipCode.value,this.countryCode.countryCode.value);
        e.preventDefault();
    }

    render() {
        return(
            <div className="search">
                {/* <input type="text" placeholder="Zip Code" value={this.props.zipCode} onChange={this.handleZipChange} />
                <input type="text" placeholder="Country Code" value={this.props.countryCode} onChange={this.handleCountryChange} /> */}
                <form onSubmit={this.handleLocationChange}>
                    <input type="text" placeholder="Zip Code" name="zipCode" ref="zipCode" />
                    <input type="text" placeholder="Country Code" name="countryCode" ref="countryCode" />
                    <button type="submit">Search</button>
                </form>
            </div>
        );
    }
}

class  WeatherTitle extends React.Component {
    render() {
        return (
            <h1>Weather in {this.props.zipCode},{this.props.countryCode}</h1>
        );
    }
}

class WeatherData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: {}
        };
    }
    componentDidMount() {
        const zipCode = this.props.zipCode;
        const countryCode = this.props.countryCode;
        const url = 'https://api.openweathermap.org/data/2.5/weather?zip=' + zipCode + ',' + countryCode + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((data) => {this.setState({
            data : data,
            })
        });
    }
    render() {
        const data = this.state.data;
        if(data.weather) {
            return(
                <h1>{data.name}</h1>
            );
        } else return null;
        // return null;
    }
}


class WeatherApp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            zipCode: "",
            countryCode: ""
        }
        // this.handleZipChange = this.handleZipChange.bind(this);
        // this.handleCountryChange = this.handleCountryChange.bind(this);
        this.handleLocationChange = this.handleLocationChange.bind(this);
    }

    // handleZipChange(zipCode) {
    //     this.setState({
    //       zipCode: zipCode,  
    //     })
    // }

    // handleCountryChange(countryCode) {
    //     this.setState({
    //       countryCode: countryCode,  
    //     })
    // }

    handleLocationChange(zipCode,countryCode) {
        this.setState({
            zipCode: zipCode,
            countryCode: countryCode
        });
    }

    render() {

        return(
            <div className="container">
                <SearchBar 
                    zipCode={this.state.zipCode}
                    countryCode={this.state.countryCode}
                    // onZipChange={this.handleZipChange}
                    // onCountryChange={this.handleCountryChange}
                    onLocationChange={this.handleLocationChange}
                />
                {/* <Clock /> */}
                <WeatherTitle zipCode={this.state.zipCode} countryCode={this.state.countryCode} />
                <WeatherData zipCode={this.state.zipCode} countryCode={this.state.countryCode} />
            </div>
        );
    }
}


ReactDOM.render(
    //<SearchBar />,
    <WeatherApp />,
    document.getElementById("root")
);
