import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import IconId from './icon.js';
import wallpaper from './wallpaper.js';
import SearchBar from './components/SearchBar/SearchBar.js';


class  WeatherTitle extends React.Component {
  render() {
      return (
          <div className="weatherTitle">
          {this.props.data.main &&
            <h1>{this.props.data.name}, {this.props.data.sys.country}</h1>
          }
          </div>
      )
  }
}

class Clock extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        date: new Date(),
      };
    }
    componentDidMount() {
        this.timerID = setInterval(
          () => this.tick(this.props.data.timezone),
          1000
        );
      }
    tick = (timezone) => {
      this.setState({
        date: this.getTime(timezone),
      });
    }

    getTime = (offset) =>
        {
            let d = new Date();
            let localTime = d.getTime();
            let localOffset = d.getTimezoneOffset() * 60000;

            // obtain UTC time in msec
            let utc = localTime + localOffset;
            // create new Date object for different city
            // using supplied offset
            let nd = new Date(utc + (1000*offset));
            return nd;
            //nd = 3600000 + nd;
            //utc = new Date(utc);
            // return time as a string
            // $("#local").html(nd.toLocaleString());
            // $("#utc").html(utc.toLocaleString());
        };

    render() {
        let hours = this.state.date.getHours();
        let minutes = this.state.date.getMinutes();
        let seconds = this.state.date.getSeconds();
        if (minutes < 10) {
            minutes = '0' + minutes;
        }
        if (seconds < 10) {
          seconds = '0' + seconds;
      }
        let ampm = 'am';
        if (hours > 12) {
            hours = hours - 12;
            ampm = 'pm';
        } else if (hours === 0) {
            hours = 12;
            ampm = 'am'
        } else if (hours === 12) {
            ampm = 'pm'
        }
        if(this.props.data.main) {
            return (
                <div className="clock">
                    <h2>{hours}:{minutes}:{seconds} {ampm}</h2>
                </div>
            );
        } else return null;
    }
}

class Cloudiness extends React.Component {
    render() {
        if(this.props.data.main) {
            let data = "";
            let arr = this.props.data.weather[0].description.split(" ");
            for (let i = 0; i < arr.length; i++) {
                data+=arr[i][0].toUpperCase() + arr[i].slice(1) + " ";
            }
            return(
                <div className="cloudiness">
                    <p>{data}</p>
                </div>
            );
        } else return null;
    }
}

class Icon extends React.Component {
    render() {
        if(this.props.data.main) {
            const id = IconId[this.props.data.weather[0].id];
            const url = "http://openweathermap.org/img/wn/" + id + "d@2x.png";
            return(
                <div className='icon'>
                    <img src={url} alt={this.props.data.weather[0].id}/>
                </div>
            );
        } else return null;
    }
}

class Temperature extends React.Component {
    render() {
        if(this.props.data.main) {
            return(
                <div className='temp'>
                    <p>{(this.props.data.main.temp-273).toFixed(2)} &#8451;</p>
                </div>
            );
        } else return null;
    }
}

class OtherData extends React.Component {
    render() {
        if(this.props.data.main) {
            return(
                <div className="otherData">
                    <p>Temperature: {(this.props.data.main.temp_min-273).toFixed(2)} &#8451; - {(this.props.data.main.temp_max-273).toFixed(2)} &#8451;</p>
                    <p>Humidity: {this.props.data.main.humidity} %</p>
                    <p>Wind: {this.props.data.wind.speed} km/hr</p>
                </div>
            );
        } else return null;
    }
}

class WeatherData extends React.Component {
    constructor(props) {
        super(props);
        this.newData = this.newData.bind(this);
        this.getMyLocation = this.getMyLocation.bind(this);
        this.getMyLocation();
    }

    getMyLocation() {
        this.props.getMyLocation();
    }

    fetchDataByZip(zipCode,countryCode) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?zip=' + zipCode + ',' + countryCode + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((wdata) => {
            this.newData(wdata);
        });
    }

    newData(data) {
        this.props.newData(data);
    }

    fetchDataByGeo(latitude,longitude) {
        const url = 'https://api.openweathermap.org/data/2.5/weather?lat=' + latitude + '&lon=' + longitude + '&appid=9b32cf97b04c03ad2e6f0960ea7bdce3';
        fetch(url)
        .then(results => results.json())
        .then((wdata) => {
            this.newData(wdata);
        });
    }

    componentDidMount() {
        if(this.props.zipCode && this.props.countryCode){
            this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        if(this.props.latitude!==null && this.props.longitude!==null){
            this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
    }


    componentDidUpdate(prevProps) {
        if (this.props.zipCode !== prevProps.zipCode || this.props.countryCode !== prevProps.countryCode) {
          this.fetchDataByZip(this.props.zipCode,this.props.countryCode);
        }
        if (this.props.latitude !== prevProps.latitude || this.props.longitude !== prevProps.longitude) {
            this.fetchDataByGeo(this.props.latitude,this.props.longitude);
        }
      }

    render() {
       //  const data = this.props.data;
        // while(!data) {
        //     return(
        //       <div className="loader">
        //         <div class="lds-hourglass"></div>
        //       </div>

        //     );
        // }
        return null;
        // else return null;
    }
}

let location,latitude,longitude;

class WeatherApp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            zipCode: "",
            countryCode: "",
            data: "",
        };
    }

    getMyLocation = () => {
        if (window.navigator && window.navigator.geolocation) {
            location = window.navigator.geolocation
        }
        if (location){
            location.getCurrentPosition(function (position) {
                latitude = (position.coords.latitude);
                longitude = (position.coords.longitude);
            });
        }        
    }

    handleSubmit = (zipCode,countryCode) => {
        this.setState({
            zipCode: zipCode,
            countryCode: countryCode
        })
    }



    newData = (data) => {
        this.setState({
            data: data,
        })
    }

    render() {
        // let styleText = {
        //     backgroundImage: "url('')",
        // };
        if (this.state.data.main){
            let url = wallpaper(this.state.data.weather[0].id);
            document.getElementById("root").style.backgroundImage = "url("+url+")";
            // styleText = {
            //     backgroundImage: "url(" + url + ")",
            // };
        }
        return(
            <div className="rootContainer">
                <div className='Container'>
                    <SearchBar 
                        zipCode={this.state.zipCode}
                        countryCode={this.state.countryCode}
                        latitude={this.state.latitude}
                        longitude={this.state.longitude}
                        onSubmit={this.handleSubmit}
                    />
                    {/* <Clock /> */}
                    <WeatherTitle zipCode={this.state.zipCode} countryCode={this.state.countryCode} data={this.state.data} />
                    <div className='leftBlock'>
                        <Clock data={this.state.data} />
                        <Cloudiness data={this.state.data} />
                        <Icon data={this.state.data} />
                        <Temperature data={this.state.data} />
                    </div>

                    <OtherData data={this.state.data} />
                    <WeatherData 
                        zipCode={this.state.zipCode} countryCode={this.state.countryCode} 
                        newData={this.newData}
                        data={this.state.data}
                        latitude={latitude}
                        longitude={longitude}
                        getMyLocation={this.getMyLocation}
                    />
                </div>
            </div>

        );
    }
}


ReactDOM.render(
    <WeatherApp />,
    document.getElementById("root")
);
