class TempUnitButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isCelcius: true,
    }
  }

  handleChange = () => {
    this.setState({
      isCelcius: !this.state.isCelcius,
    });
    // console.log(this.state.isCelcius);
  }

  render() {
    // console.log(this.state.isCelcius);
    return (
      <div className="unitConverter">
        <div className="switchContainer">
          <label>
            <input type="checkbox" checked={this.state.isCelcius} onChange={this.handleChange} className="switch" />
            <div className="slider">
              <div className="circle"></div>
            </div>
          </label>
        </div>
      </div>
    );
  }
}

export default TempUnitButton;
