import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import city from './city.js';

class Weather extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            weather : [],
        };
    }

    componentDidMount() {
        fetch('https://samples.openweathermap.org/data/2.5/weather?q={props.city.name}')
        .then(results => {
            return results.json();
        }).then(data => {
            let weather = data.results.map((wdata) => {
                return(
                <div key={wdata.results.id}> {wdata.results.weather} </div>);
            })
            this.setState({weather:weather});
        })

    }
    render() {
        return(
            <h1>{this.state.weather}</h1>
        );
    }
}

ReactDOM.render(
    <Weather city={city[1]} />,
    document.getElementById("root")
);
